const splashScreenContent=document.getElementsByClassName("splashScreen__content")[0];splashScreenContent.addEventListener("click",()=>{const e=document.getElementsByClassName("splashScreen__image")[0],t=getComputedStyle(e);e.style.maxHeight=t.height,e.style.maxWidth=t.width,e.classList.add("animate"),splashScreenContent.classList.add("animate"),setTimeout(()=>{document.getElementById("splashScreen").style.display="none"},700)});